// Generated by `yarn new:mutationMock`
import { graphql, HttpResponse } from 'msw';

import { data } from './data';
import type { Mutation, Options } from './type';

export const refreshTokenMutation = (options?: Options) => {
  return graphql.mutation<Mutation>('RefreshToken', () => {
    if (options?.networkError) {
      return HttpResponse.json(
        {
          errors: [
            {
              message: 'Network request failed',
            },
          ],
        },
        { status: 500 }
      );
    }

    return HttpResponse.json({ data: data(options?.res, options?.deepMergeOptions) });
  });
};
